package mypackage;

public class Elseifstatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String s = "ello";
if(s=="Hello") {
	System.out.println("String is Hello");
}else if(s=="hello") {
	System.out.println("String is hello");
}else if(s=="Bye") {
	System.out.println("String is Bye");
	}else {
		System.out.println("please give correct string");
	}

}
}